                                        arr=arr+this.structDefine.tableData[i].abcName
                                    }
                                    console.log(treeNode.pathRule)
                                    
                                
                                    if(treeNode.pathRule!=undefined){
                                        let path=JSON.parse(treeNode.pathRule);
                                        for(var i=0;i<path.length;i++){
                                            if(arr.indexOf(path[i])!=-1){
                                                for(var j=0;j<this.structDefine.tableData.length;j++){
                                                    if(path[i]==this.structDefine.tableData[j].abcName){
                                                        this.mountDefine.mountData+=this.structDefine.tableData[j].columnName;
                                                    }
                                                }
                                            }else{
                                                this.mountDefine.mountData+=path[i];
                                            }
                                        }
                                    
                                    this.mountDefine.mountDatas=[].concat(path); 
