                console.log(self.znodeData);
                $.fn.zTree.init($("#templeteTree"), self.setting, self.znodeData);
                var treeObj = $.fn.zTree.getZTreeObj("templeteTree");
                var nodes = treeObj.getNodesByParam("writeable", 0, null);
                console.log(nodes)
                for(var x=0;x<nodes.length;x++){
                    self.findParentNodes(nodes[x].id)
                }
                
            }).catch(function(err){
