            if(currentRow.sortType != 0){
                //将排序级别在该字段之后的排序级别重新设置，即减1
                this.dropSortLevel(currentRow.sortLevel)
                // //设置字段排序级别为0，即不参与排序
                // currentRow.sortLevel = 0;
                // //将排序方式设为0
                // currentRow.sortType = 0;

