        dropSortLevel(sortLevel){
            console.log("将索引为"+sortLevel+"的")
            var newData = this.sortDefine.data.map((item)=>{
                if(item.sortLevel == sortLevel){
                    item.sortLevel = 0;
                    item.sortType = 0;
                    return item;
                }else if(item.sortLevel > sortLevel){
                    item.sortLevel--; 
                    return item;
                }else{
                    return item;
                }
            })
            console.log(newData);
