                        ).then((res1)=>{
                            console.log(res1);     
                            this.operation('success','成功删除'+i+'条');   
                            this.structDefine.tableData=res1.data.columns;
                            this.structDefine.selectGroup=[];
                            
                        }).catch((err)=>{

                        })
                        }
                    ).catch(
                        (err)=>{
                            console.log(err)
                        }
                    )
                }
            }          
        },
        backDeskClick(){
            this.$router.push({path:"/"});
        }
        // zTreeOnClick(event,treeId,treeNode){
        //     console.log(treeNode)
        // }
    },
    watch:{
        'structDefine.selectGroup':function(selection,oldSelection){
            if(selection.length > 0){
                this.structDefine.columnPros.columnId = selection[0].id;
                this.structDefine.columnPros.columnName = selection[0].columnName;
                this.structDefine.columnPros.abcName = selection[0].abcName;
                this.structDefine.columnPros.columnType = selection[0].columnType;
                this.structDefine.columnPros.columnLength = selection[0].columnLength;
                this.structDefine.columnPros.sortType = selection[0].sortType;
                this.structDefine.columnPros.sortLevel = selection[0].sortLevel;
            }
        }
    }
}
</script>

