                            res.data.anJianData[i].fileCount=0
                        }
                        
                    }
                    this.bigData.dataList.loading=false
                    this.bigData.dataList.columnsDatas=res.data.columns
                    this.bigData.dataList.tableDatas.push(data)
                    res.data.anJianColumns.unshift(
                        {
                            field: 'fileCount', 
                            title:'文件数量', 
                            width: 15, 
                            titleAlign: 'center',
                            columnAlign:'center',
                            isResize:true
                        }
                    )
                    this.bigData.dataList.columnsAjDatas=res.data.anJianColumns
                    this.bigData.dataList.tableAjDatas=res.data.anJianData
                    this.bigData.AjdataListShow=true
                }
            }).catch((err)=>{
                console.log(err)
            })
