        handleChange(val) {     
            console.log('active item:', val);
            this.$axios.post('/api/api/templetTree/node/getChildNode',{
                id:val[val.length-1]
            }).then((res)=>{
                console.log("xxxxxxxxxxx",res)
                for(var i=0;i<res.data.nodes.length;i++){
                    res.data.nodes[i].children=[];       
                }
                this.addChild(val,val.length,this.options,res.data.nodes,0)  
                this.bigData.rootNodeId=val[val.length-1];         
            }).catch((err)=>{
                console.log(err)
            })
        },
        handleClick(){
            this.tableData=[];
            if(this.bigData.rootNodeId==''){
                this.operation('warning','请选择搜索范围')
            }else{
                this.$axios.post('/api/api/archives/data/bigQuery',{
                    column:makePy(this.bigData.column)[0],
                    keyword:this.bigData.keyword,
                    rootNodeId:this.bigData.rootNodeId,
                }).then((res)=>{
                    console.log(res)
                    if(res.data.datas.length!=0){

                        res.data.datas.map(item=>{
                            item.value = item[makePy(this.bigData.column)[0]];
                            this.tableData.push(item);
                            })

                    }else{
                        alert('搜索结果为空')
                    }
                }).catch((err)=>{
                    console.log(err)
                })
            }

            // 
            
            
            
        },
        addChild(val,num,options,data,index){
            if(num==1){
                for(var j=0;j<options.length;j++){
                    if(options[j].id==val[index]){
                        options[j].children=data; 
                    }
                }      
            }else{ 
                for(var j=0;j<options.length;j++){
                    
                    if(options[j].id==val[index]){  
                        num--;
                        index++;
                        console.log("111",options[j].children)
                        this.addChild(val,num,options[j].children,data,index)
                    }
                }  
                
            }
              
        },
        
